class Formulario {
    constructor(nombre, correo, id) {
        this.nombre = nombre;
        this.correo = correo;
        this.id = id;
    }


    // Método para mostrar los datos recibidos
    mostrarDatos() {
        console.log(`Nombre: ${this.nombre}`);
        console.log(`Correo: ${this.correo}`);
        console.log(`ID: ${this.id}`);
    }

    // Método para procesar el formulario
    procesarFormulario() {
        if (this.validarDatos()) {
            this.mostrarDatos();
        } else {
            alert("Por favor, rellena todos los campos correctamente.");
        }
    }
}

// Función para recibir los datos del formulario y crear una instancia de Formulario
function recibirDatos() {
    // Obtener los valores de los campos del formulario
    const nombre = document.getElementById("nombre").value;
    const correo = document.getElementById("correo").value;
    const id = document.getElementById("id").value;

    // Crear una nueva instancia de la clase Formulario
    const formulario = new Formulario(nombre, correo, id);

    // Procesar el formulario
    formulario.procesarFormulario();
}
